<?php/** * The template for displaying all single posts. * * @package WordPress * @subpackage JBlog * @since JBlog 1.0 *  */get_header(); ?>	<div id="jblog-page-wrapper" class="row single">		<div id="jblog-content" role="main" class="col-md-8">			<?php if ( have_posts() ) while ( have_posts() ) : the_post(); 						$key = 'portfolio_url';						$themeta = get_post_meta($post->ID, $key, TRUE);						$aend = '';						$abegin = '';						if($themeta != '') {							$abegin = '<a href="'.$themeta.'" target="_blank">';											$aend = '</a>';						}						if (has_post_thumbnail()) { ?>				<div id="jblog-post-image-container" class="jblog-imgliquid">					<?php echo $abegin; ?><?php echo the_post_thumbnail('large'); ?><?php echo $aend; ?>				</div>			<?php } ?>			<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>				<header>											<?php echo $abegin; ?><h1><?php the_title(); ?></h1><?php echo $aend; ?>					<p class="jblog-entry-meta">						<?php jblog_posted_on(); ?>					</p><!-- .entry-meta -->				</header>				<div class="jblog-entry-content">					<?php the_content(); ?>					<?php wp_link_pages( array( 'before' => '<div class="page-link">' . __( 'Pages:', 'jblog' ), 'after' => '</div>' ) ); ?>				</div><!-- .entry-content -->				<footer class="jblog-entry-utility">					<?php jblog_posted_in(); ?>					<?php edit_post_link( __( 'Edit', 'jblog' ), '<span class="edit-link">', '</span>' ); ?>				</footer><!-- .entry-utility -->			</article><!-- #post-## -->			<nav id="jblog-nav-below" class="navigation row">				<div class="jblog-nav-previous col-xs-6"><?php previous_post_link( '%link', '<span class="meta-nav">' . _x( '&larr;', 'Previous post link', 'jblog' ) . '</span> %title' ); ?></div>				<div class="jblog-nav-next col-xs-6"><?php next_post_link( '%link', '%title <span class="meta-nav">' . _x( '&rarr;', 'Next post link', 'jblog' ) . '</span>' ); ?></div>			</nav><!-- #nav-below -->			<hr/>			<div class="well">				<?php 				comments_template('/comments.php'); ?>			</div>						<?php endwhile; // end of the loop. ?>					</div><!-- #content -->		<?php get_sidebar(); ?>	</div><!-- #container --><?php get_footer(); ?>